components.content:
:root {
  --a: #7c3aed;
  --b: #22d3ee;
  --c: #a3ff12;
  --x: 50%;
  --y: 50%;
}

* {
  box-sizing: border-box;
}

html,
body {
  height: 100%;
}

body {
  margin: 0;
  color: rgba(255, 255, 255, 0.92);
  font-family: ui-sans-serif, system-ui, -apple-system, Segoe UI, Roboto, Helvetica, Arial;

  /* Animated gradient layer */
  background: radial-gradient(900px 600px at 20% 20%, rgba(255, 255, 255, 0.07), transparent 60%),
    conic-gradient(from 180deg, var(--a), var(--b), var(--c), var(--a));
  background-size: 100% 100%, 140% 140%;
  animation: drift 10s ease-in-out infinite;

  /* Spotlight (follows pointer via CSS vars) */
  position: relative;
  overflow: hidden;
}

body::before {
  content: "";
  position: absolute;
  inset: -30%;
  pointer-events: none;
  background: radial-gradient(300px 300px at var(--x) var(--y), rgba(255, 255, 255, 0.22), transparent 60%);
  mix-blend-mode: screen;
  filter: blur(6px);
}

body::after {
  content: "";
  position: absolute;
  inset: 0;
  pointer-events: none;
  background: radial-gradient(800px 500px at var(--x) var(--y), rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0.72));
}

@keyframes drift {
  0% {
    background-position: 0% 0%, 30% 50%;
  }
  50% {
    background-position: 0% 0%, 70% 40%;
  }
  100% {
    background-position: 0% 0%, 30% 50%;
  }
}

.wrap {
  position: relative;
  z-index: 1;
  height: 100%;
  display: grid;
  place-content: center;
  gap: 10px;
  padding: 24px;
  text-align: center;
}

h1 {
  margin: 0;
  font-size: clamp(30px, 5vw, 54px);
  letter-spacing: -0.03em;
}

p {
  margin: 0;
  color: rgba(255, 255, 255, 0.70);
}

button {
  justify-self: center;
  padding: 10px 14px;
  border-radius: 12px;
  border: 1px solid rgba(255, 255, 255, 0.22);
  background: rgba(0, 0, 0, 0.28);
  color: rgba(255, 255, 255, 0.92);
  cursor: pointer;
}

@media (prefers-reduced-motion: reduce) {
  body {
    animation: none;
  }
}

(() => {
  const root = document.documentElement;
  const palettes = [
    ['#7c3aed', '#22d3ee', '#a3ff12'],
    ['#fb7185', '#38bdf8', '#fbbf24'],
    ['#34d399', '#60a5fa', '#a78bfa'],
  ];
  let idx = 0;

  function setPalette(i) {
    const [a, b, c] = palettes[i % palettes.length];
    root.style.setProperty('--a', a);
    root.style.setProperty('--b', b);
    root.style.setProperty('--c', c);
  }

  function setSpotlight(clientX, clientY) {
    root.style.setProperty('--x', `${(clientX / innerWidth) * 100}%`);
    root.style.setProperty('--y', `${(clientY / innerHeight) * 100}%`);
  }

  addEventListener('mousemove', (e) => setSpotlight(e.clientX, e.clientY));
  addEventListener(
    'touchmove',
    (e) => {
      const t = e.touches && e.touches[0];
      if (t) setSpotlight(t.clientX, t.clientY);
    },
    { passive: true },
  );

  function next() {
    idx = (idx + 1) % palettes.length;
    setPalette(idx);
  }

  document.getElementById('next')?.addEventListener('click', next);
  addEventListener('click', (e) => {
    if (e.target?.id !== 'next') next();
  });

  setPalette(idx);
  setSpotlight(innerWidth * 0.5, innerHeight * 0.5);
})();
